		var pcPgBackCount = 0;

		payment = {
			paySetup: function (data) {
                function addWarningMessage($input, message) {
                    var $warningMessage = $input.parent().parent('.extra-info__wrapper').find('.extra-info__warning');
                    $warningMessage.show();
                    $warningMessage.text(message);

                    uxlogger.log({
                       area:'extra-info.pc',
