                }
                $('.dealExtraInquiry').on('input', '.extra-info__wrapper .etcInfo', function (e) {
                    var $warningMessage = $(this).parent().parent('.extra-info__wrapper').find('.extra-info__warning');
                    $warningMessage.hide();
                    $warningMessage.text('');
                });
                
				var etcInfoInput = $("input.etcInfo");
				var isValidEtc = true;
				etcInfoInput.each(function () {
					if ($(this).val() === '') {
						if (isValidEtc) {
                            addWarningMessage($(this), "입력해 주세요.");
							$(window).scrollTop(0);
							$(this).focus();
						}
						isValidEtc = false;
					} else if ($(this).val().indexOf('/') > -1) { // consumer 약속구분자로 지정되어 있어 입력 제어
                        if (isValidEtc) {
                            addWarningMessage($(this), "아래 특수문자만 입력 가능합니다. 다시 확인해 주세요.");
