                // 결제 전, 결제 데이터 제어를 위한 핸들러 추가
                function orderDataHandler(overrideData) {
                    $.extend(data, overrideData);
                }

                // 선택한 카드사의 기등록된 카드로 결제를 유도하는 모달
                var registeredCardOfferModal = Bundle.registeredCardOfferModal;
                registeredCardOfferModal.inject({
