                function order() {
                    // 신용/체크카드로 결제 시, Coupay or LegacyPayment 여부 설정
                    var rocketCardInstance = orderController.controllers.RocketpayCard.instance;
                    if (data['payType'] === "ROCKET_CARD" && rocketCardInstance.useLegacyCard()) {
                        orderDataHandler({
                            payType: "CARD"
                        });
                    }

                    payment.startPreOrder();

                    $.ajax({
                        type: "POST",
                        url: url,
                        data: data,
                        contentType: 'application/x-www-form-urlencoded; charset=utf-8;',
                        success: function (msg) {
                            var resultObj = JSON.parse(msg);
                            var orderResultStatus = resultObj.status;
                            var bookingStatus = resultObj.bookingStatus || '';
                            var type = resultObj.type;
                            var vendorItemName = resultObj.vendorItemName;

                            if ('' !== bookingStatus) {
                                // booking
                                alert(resultObj.msg);
                                document.location.replace(_.unescape(resultObj.redirectUrl));
                                return false;
                            }
                            if ('OUT_OF_STOCK' === orderResultStatus) {
                                payment.finishPreOrder();
                                if (resultObj.redirectUrl) {
                                    Bundle.ajaxExceptionHandler.alertAndRedirectNoJson(resultObj.message, resultObj.redirectUrl);
                                } else {
                                    alert(resultObj.message);
                                    history.back();
                                }
                                return;
                            }
                            if ('INVALID_ITEM' === orderResultStatus) {
                                payment.finishPreOrder();
                                alert(resultObj.message);
                                return;
                            }
                            if ('ALERT_REDIRECT' === orderResultStatus) {
                                payment.finishPreOrder();
                                alert(resultObj.msg);
                                if (typeof(resultObj.redirectUrl) !== 'undefined' || resultObj.redirectUrl !== ''){
                                    document.location.replace(_.unescape(resultObj.redirectUrl));
                                }
                                return false;
                            }
                            if ('INCLUDE_UNAVAILABLE_VENDORITEM' === orderResultStatus) {
                                payment.finishPreOrder();
                                Bundle.remoteAreaDelivery.buildView(resultObj);
                                return;
                            }
                            if ('CHANGED_REMOTE_AREA_DELIVERY_PRICE' === orderResultStatus) {
                                Bundle.remoteAreaDelivery.buildView(resultObj, {
                                    finishPayment : function() {
                                        payment.finishPreOrder()
                                    },
                                    startPayment : function() {
                                        $('#paymentBtn').trigger('click');
                                    }
                                });
                                return;
                            }
                            if ('GOOD' !== orderResultStatus) {
                                payment.finishPreOrder();
                                fraudDetector.showErrorDialog(type, vendorItemName, resultObj.htmlContent, resultObj.htmlType);
                                return;
                            }
                            var paymentRequestParam = resultObj.object;
                            payment.orderSuccess(paymentRequestParam, data);
                            pcPgBackCount ++;
                        },
                        error: function (jqXHR, status, errorThrown) {
                            var payType = orderController.payBox.getPayType();

                            
                                var couponLogData = orderController.getCouponLogData();
                            
                            Bundle.weblog.orderStatus({
                                payType: payType,
                                
                                    hasCoupon: 0,
                                    useCoupon: couponLogData.useCoupon,
                                    isBestOffer: couponLogData.isBestOffer,
                                    couponDiscountPrice: couponLogData.couponDiscountPrice,
                                    totalItem: couponLogData.totalItem,
                                    totalPrice: couponLogData.totalPrice,
                                
                                status: 'error'
                            });

                            if (jqXHR.status === 400) {
                                var responseJson;
                                if (jqXHR.responseText === 'object') {
                                    responseJson = jqXHR.responseText;
                                } else {
                                    responseJson = JSON.parse(jqXHR.responseText);
                                }
                                if (responseJson.data && responseJson.data.message === "INVALID_DAWN_ONLY_REGION_OOS") {
                                    alert("현재 주소로 배송가능한 로켓프레시 상품의 재고가 부족합니다.");//"현재 주소로 배송가능한 재고가 없습니다.");
                                } else if (responseJson.data && responseJson.data.message === "INVALID_DAWN_ONLY_REGION_INSUFFICIENT") {
                                    alert("현재 주소로 배송가능한 로켓프레시 상품의 재고가 부족합니다.");
                                } else {
                                    Bundle.ajaxExceptionHandler.alertAndRedirect(jqXHR.responseText);
                                }

                            } else if (jqXHR.status === 401) {
                                alert(jqXHR.responseText);
                                document.location.href = "https://login.coupang.com/login/login.pang?rtnUrl=https%3A%2F%2Forder.coupang.com%2Fdirect%2Fcheckout%2F1577115084901%3Fitem%255B%255D%3D4831156786%253A1";
                            } else if (jqXHR.status === 420) {
                                var resultObj = {errorType:''};

                                try {
                                    resultObj = JSON.parse(jqXHR.responseText);
                                }catch(e) {}

                                if (jqXHR.responseText === 'INVALID_CART') {
                                    alert("주문 상품이 변경되었습니다. 다시 주문해 주세요.");
                                    document.location.href = "../../../../../";
                                } else if (resultObj.errorType === 'TOTAL_SALE_PRICE_CHANGE') {
                                    Bundle.totalSalePriceChange.notice(resultObj.message, resultObj.price, '1577115084901');
                                    document.location.reload();
                                } else if (resultObj.errorType === 'RESERVATION_ERROR') {
                                    alert(resultObj.message);
                                    document.location.href = resultObj.returnUrl;
                                } else if (resultObj.errorType === 'BUNDLE_CUT_OFF_DATE') {
                                    if (resultObj.deliveryType === 'OVERNIGHT') {
                                        Bundle.modal.alert({
                                            title: '배송예정일 변경',
                                            message: resultObj.message,
                                            callback: {
                                                confirm: function() {
                                                    document.location.reload();
                                                }
                                            }
                                        });
                                    } else {
                                        Bundle.modal.yesOrNo({
                                            title: '배송예정일 변경',
                                            message: resultObj.message,
                                            callback: {
                                                yes: function() {
                                                    payment.finishPreOrder();
                                                    Bundle.checkoutForm.formSubmit();
                                                    Bundle.modal.close();
                                                    return false;
                                                },
                                                no: function() {
                                                    document.location.reload();
                                                }
                                            }
                                        });
                                    }
                                } else {
                                    var message = jqXHR.responseText;
                                    var regex = /alert\s*\(["']\s*([\w\W]+?)\s*["']\);?/;
                                    if (regex.test(message)) {
                                        message = message.match(regex)[1];
                                    }
                                    alert(message);
                                }
                            } else {
                                alert("주문중 오류가 발생하였습니다. 다시 시도해 주세요.");
                            }
                            payment.finishPreOrder();
                        }
                    });

                    
                        var couponLogData = orderController.getCouponLogData();
                    

                    Bundle.weblog.purchaseClick({
                        entryType: 'direct',
                        
                            hasCoupon: 0,
                            useCoupon: couponLogData.useCoupon,
                            isBestOffer: couponLogData.isBestOffer,
                            couponDiscountPrice: couponLogData.couponDiscountPrice,
                            totalItem: couponLogData.totalItem,
                            totalPrice: couponLogData.totalPrice,
                        
                        hasPoint: false,
                        usePoint: (data.usePoint ? data.usePoint : 0),
                        isPointInPaybox: false,
                        payType: orderController.payBox.getPayType(),
                        payCode: orderController.payBox.getPayCode(),
                        isLoyaltyMember : true,
                        pageLoadType: (pcPgBackCount == 0 ? 'load' : 'pg_back'),
                        pgBackCount: (pcPgBackCount == 0 ? null : pcPgBackCount),
                        deliveryAddressDetailSrl: (data.deliveryDetailSrl != null ? parseInt(data.deliveryDetailSrl) : null),
                        defaultDeliveryAddress: (data.defaultDeliveryAddress != null ? data.defaultDeliveryAddress : null),
                        buttonType: data.buttonType,
                        checkoutType: 'DEFAULT',
                        ccidAmount: data.ccidAmount || 0,
                        ccidOverThreshold: false,
                        readyToGetCCID: data.ccidAmount > 0
                    });
                }
            },
			orderSuccess: function(paymentRequestParam, data) {
				var payType = orderController.payBox.getPayType();

                
                var couponLogData = orderController.getCouponLogData();
                
                Bundle.weblog.orderStatus({
                    payType: payType,
                    status: 'success',
                    paymentToken: paymentRequestParam.paymentToken,
                    checkoutType: 'DEFAULT',
                    defaultPayType : 'ROCKET_BALANCE',
                    
                    hasCoupon: 0,
                    useCoupon: couponLogData.useCoupon,
                    isBestOffer: couponLogData.isBestOffer,
                    couponDiscountPrice: couponLogData.couponDiscountPrice,
                    totalItem: couponLogData.totalItem,
                    totalPrice: couponLogData.totalPrice,
                    
                    hasPoint: false,
                    isRocketPayMember : true,
                    orderId: paymentRequestParam.orderId,
                    receiptData : true,
                    receiptActive : true,
                    moneyBalance: 2500,
                    usePoint: (data.usePoint ? data.usePoint : 0),
                    realNameVerification: true,
                    financeCode: paymentRequestParam.financeCode,
                    payMethodId: paymentRequestParam.rocketPayMemberAccountId == null ? 'null' : paymentRequestParam.rocketPayMemberAccountId
                });

				payment.finishPreOrder();

                Bundle.metricCounterLog.log('paymentStart', {isDirectOrder: 'Y'});

				orderController.controllers.CoupangPayment.instance.setCoupangPayment(paymentRequestParam);
			},
			//검색에 노출되지 않음 지워야할 것.
			hideNonActiveXIframe: function () {
				$('#nonActiveXIframe').hide();
				payment.finishPreOrder();
			},
			startPreOrder: function () {
				$('#modal-loading').modal('show');
				//$('#btn_all').hide();
			},
			finishPreOrder: function () {
				$('#modal-loading').modal('hide');
                //$('#btn_all').show();
			},
            payResult: function (rtn) {
                if (rtn == 'NOT_LOGIN') {
                    alert("세션이 종료 되었습니다.");
                    document.location.href = "https://login.coupang.com/login/login.pang?rtnUrl=https://order.coupang.com/direct/checkout/1577115084901?item%5B%5D=4831156786%3A1";
                } else {
                    alert(rtn);
                }
                orderController.controllers.CoupangPayment.instance.finishPaymentDialogue();
            },
            orderComplete: function (orderId) {
                payment.finishPreOrder();
                document.location.href = '/orderResult.pang?orderId=' + orderId;
            }
		};

		fraudDetector = {
			showErrorDialog: function (type, vendorItemName, htmlContent, htmlType) {
                if("PAGE" === htmlType){
                    $("#fraudDetectorErrorDialogPageWrapper").html(htmlContent).modal('show');
                    return ;
                }
                $('#messageVendorItemName').hide();
				$('#messageOrderLimit').hide();
				$('#messageCouponLimit').hide();
				$('#modalViolationErrorDialogHTMLContent').hide()
				if (type === 'ORDER_LIMIT') {
					if (vendorItemName) {
						$('#messageVendorItemName').html(vendorItemName);
						$('#messageVendorItemName').show();
					}
					$('#messageOrderLimit').show();
				}
				if (type === 'COUPON_LIMIT') {
					$('#messageCouponLimit').show();
				}

				if(htmlContent && htmlContent.trim()) {
					$('#modalViolationErrorDialogHTMLContent').html(htmlContent).show();
					$("#modalViolationErrorDialogLegacyContent").hide();
				}else{
					$("#modalViolationErrorDialogLegacyContent").show();
					$('#modalViolationErrorDialogHTMLContent').hide();
				}
				$('#modalViolationErrorDialog').modal('show');
			},

			hideErrorDialog: function () {
				$('#modalViolationErrorDialog').modal('hide');
			},

			onClickContactBtn: function () {
				fraudDetector.hideErrorDialog();
				document.location.href = "http://www.coupang.com/voiceOfCustomer.pang";
			},

			onClickCartBtn: function () {
				fraudDetector.hideErrorDialog();
				document.location.href = "http://www.coupang.com/cartView.pang";
			}
		};

		//        $(document).ready(function () {
		//            option.setTotalPayPrice();
		//        });
	</script>
    

